import React, { useState } from 'react';
import {
  SafeAreaView,
  StyleSheet,
  View,
  Text,
  TextInput,
  TouchableOpacity,
  ActivityIndicator,
  Image,
} from 'react-native';

const LoginScreen = () => {
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const loginEmployee = () => {
    // Perform login logic here
    setIsLoading(true);
    // Mock login request with setTimeout
    setTimeout(() => {
      setIsLoading(false);
      console.log(`Login pressed with phone: ${phone} and password: ${password}`);
    }, 2000);
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.logoContainer}>
        <Image
          source={require('../assets/logoapp.png')}
          style={styles.logo}
        />
      </View>

      <View style={styles.formContainer}>
        <TextInput
          style={styles.input}
          onChangeText={text => setPhone(text)}
          value={phone}
          placeholder='Phone'
          keyboardType='phone-pad'
          textContentType='telephoneNumber'
        />
        <TextInput
          style={styles.input}
          onChangeText={text => setPassword(text)}
          value={password}
          placeholder='Password'
          secureTextEntry={true}
          textContentType='password'
        />
        <TouchableOpacity
          style={styles.button}
          onPress={loginEmployee}
          disabled={isLoading}
        >
          {isLoading ? (
            <ActivityIndicator color='#fff' size='small' />
          ) : (
            <Text style={styles.buttonText}>Login</Text>
          )}
        </TouchableOpacity>
      </View>

      <TouchableOpacity style={styles.registerButton} onPress={() => console.log('Register pressed')}>
        <Text style={styles.registerText}>Don't have an account? Register here</Text>
      </TouchableOpacity>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#fff',
  },
  logoContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 50,
  },
  logo: {
    width: 200,
    height: 200,
  },
  formContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    width: '80%',
  },
  input: {
    width: '100%',
    height: 40,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    paddingLeft: 10,
    marginBottom: 20,
  },
  button: {
    width: '100%',
    height: 40,
    backgroundColor: '#2a9df4',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 5,
  },
  buttonText: {
    color: '#fff',
    fontSize: 18,
  },
  registerButton: {
    position: 'absolute',
    bottom: 20,
  },
});


export default LoginScreen;
