import { View, Text, ScrollView, Image, TextInput, FlatList, StyleSheet, TouchableOpacity } from 'react-native'
import React, { useLayoutEffect,useEffect, useState } from 'react'
import db from '../db/firebase';

import { useNavigation } from '@react-navigation/native'

const HomeScreen = () => {

  const navigation = useNavigation();
  useLayoutEffect(() => {
    navigation.setOptions({
      headerShown: false,
    });
  }, []);

  const [dataTop, setDataTop] = useState([]);
  useEffect(() => {
    const unsubscribe = db.collection('Restaurant')
      .onSnapshot((snapshot) => {
        const restaurantData = snapshot.docs.map((doc) => ({
          id: doc.id,
          data: doc.data().data,
        }));
        setDataTop(restaurantData);
      });

    return () => unsubscribe();
  }, []);
  const dataCate = [
    { id: '1', title: 'Item 1', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
    { id: '2', title: 'Item 2', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
    { id: '3', title: 'Item 3', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
    { id: '4', title: 'Item 4', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
    { id: '5', title: 'Item 5', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
  ];
  const renderItemCate = ({ item }) => (
    <TouchableOpacity onPress={() => navigation.navigate('DetailCate', { itemId: item.id })}>
      <View className="flex-row">
        <View className="flex-1 mt-2 w-24 h-28 items-center ">
          <Image source={{ uri: item.image }}
            className="w-20 h-20 rounded-full object-cover "
          />
          <Text className="mt-2 font-semibold">{item.title}</Text>
        </View>


      </View>
    </TouchableOpacity>
  );
  const renderItemTop = ({ item }) => (
    <TouchableOpacity onPress={() => navigation.navigate('DetailRes', { itemId: item.id })}>

      <View className="flex-row mt-2 mb-2">
        <Image source={{ uri: item.image }}
          className="w-32 h-20  object-cover "
        />
        <View className="ml-2">
          <Text className="font-semibold">{item.name}</Text>
          <Text className="font-semibold">{item.title}</Text>
          <Text className="font-semibold">{item.title}</Text>

        </View>

      </View>
    </TouchableOpacity>

  );

  return (
    <View className="bg-white flex-1 px-6">
      <View >
        <View className="flex-row mt-10">
          <View>
            <Text className="text-[#FF6813] text-[24px] font-semibold">FLPut App</Text>
            <Text className="text-[18px]">Hi Trung</Text>
          </View>
          <View className="right-0 absolute mt-2">
            <Image source={require('../assets/logoapp.png')} className="w-12 h-12 rounded-full" />
          </View>
        </View>
        <View className="mt-3">
          <Image source={require('../assets/banner.png')} className="max-w-full h-[180px] rounded-[40px]" />
        </View>
        <View className="mt-3">
          <TextInput
            className="border-2 border-[#ccc] h-[40px] rounded-full px-5 "
            placeholder="Tìm kiếm"
          />
        </View>
        <Text className="mt-3 text-[18px] font-semibold">Danh Mục</Text>
        <FlatList
          horizontal={true} // Thiết lập FlatList ngang
          data={dataCate}
          renderItem={renderItemCate}
          keyExtractor={(item) => item.id}
          contentContainerStyle={styles.container}
        />

      </View>
      <Text className="mt-2 text-[18px] font-semibold">Top List</Text>
      <FlatList
        data={dataTop}
        renderItem={renderItemTop}
     
        contentContainerStyle={styles.container}
      />
    </View>


  )
}

const styles = StyleSheet.create({
  container: {
    paddingVertical: 5,
  },

});
export default HomeScreen
