import { View, Text, Image,FlatList } from 'react-native'
import React, { useLayoutEffect } from 'react'
import { useNavigation } from '@react-navigation/native'
import { Ionicons, AntDesign, MaterialIcons } from '@expo/vector-icons';
import BackButton from '../components/BackButton';


const DetailRestaurant = ({ route }) => {
    const { itemId } = route.params;
    const navigation = useNavigation();
    useLayoutEffect(() => {
        navigation.setOptions({
            headerShown: false,
        });
    }, []);
    const handleBackPress = () => {
        navigation.goBack();
    };
    const dataLQ = [
        { id: '1', title: 'Item 1', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
        { id: '2', title: 'Item 2', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
        { id: '3', title: 'Item 3', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
        { id: '4', title: 'Item 4', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
        { id: '5', title: 'Item 5', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
    ];

    return (

        <View className="flex-1 bg-white relative">

            <View>
                <Image source={require('../assets/haisan.jpg')} className="max-w-full h-[200px]" resizeMode="stretch" />
            </View>
            <View className="mt-2 absolute">
                <BackButton onPress={handleBackPress} />
            </View>
            <View className="px-2 mt-2">
                <Text className="font-semibold text-[18px]">Nhà hàng:hhhhhhhhhhhhhhhhhhhhhhhhhh</Text>
                <View className="flex-row mt-2">
                    <Ionicons name="location" size={20} color="black" />
                    <Text className="ml-2">Địa chỉ</Text>
                </View>
                <View className="flex-row mt-2">
                    <AntDesign name="clockcircle" size={20} color="black" />
                    <Text className="ml-2">Thời gian</Text>
                </View>
                <View className="flex-row mt-2">
                    <MaterialIcons name="attach-money" size={20} color="black" />
                    <Text className="ml-2">Giá </Text>
                </View>
                <View className="flex-row mt-2">
                    <Ionicons name="pricetags-sharp" size={20} color="black" />
                    <Text className="ml-2">Giảm giá</Text>
                </View>
                <Text className="mt-5 font-semibold text-[20px]">Của hàng liên quan</Text>
                <FlatList
                    data={dataLQ}
                    renderItem={renderItemTop}
                    keyExtractor={(item) => item.id}
                    contentContainerStyle={styles.container}
                />



            </View>



        </View>
    );
}

export default DetailRestaurant
