import React, { useState, useLayoutEffect } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, SafeAreaView, TextBase } from 'react-native';
import { useNavigation } from '@react-navigation/native'

const LoginScreen = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const navigation = useNavigation();
  useLayoutEffect(() => {
    navigation.setOptions({
      headerShown: false,
    });
  }, []);

  const handleLogin = () => {
    // handle login logic here
  };

  return (

    <SafeAreaView className="flex-1">
      <View style={styles.container}>
        <Text style={styles.title}>FLPut App</Text>
        <Text style={styles.title1}>Chào bạn</Text>
        <TextInput
          style={styles.input}
          placeholder="Số điện thoại"
          value={username}
          onChangeText={setUsername}

          keyboardType='phone-pad'
          textContentType='telephoneNumber'
        />
        <TextInput
          style={styles.input}
          placeholder="Mật khẩu"
          value={password}
          onChangeText={setPassword}
          secureTextEntry
        />
        <TouchableOpacity style={styles.button} onPress={handleLogin}>
          <View className="flex-1 items-center justify-center">
            <Text style={styles.buttonText}>Đăng Nhập</Text>
          </View>

        </TouchableOpacity>
        <View className="flex-row mt-2">
          <Text className=" text-[16px]">Không có tài khoản? </Text>
          <Text className="text-[#FF6813] font-semibold  text-[16px]">Đăng ký</Text>
        </View>
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#FF6813',
  },
  title1: {
    fontSize: 20,
    marginBottom: 40,

  },
  input: {
    width: '80%',
    height: 50,
    borderWidth: 2,
    borderColor: '#ccc',
    borderRadius: 4,
    paddingHorizontal: 10,
    marginBottom: 20,
  },
  button: {
    backgroundColor: '#FF6813',
    width: '80%',
    height: 50,
    borderRadius: 4,
    paddingVertical: 10,
    paddingHorizontal: 20,

  },
  buttonText: {
    fontSize:16,
    alignItems: 'center',
    color: 'white',
    fontWeight: 'bold',
  },
});

export default LoginScreen;
