import { View, Text, ScrollView, Image, TextInput, FlatList, StyleSheet } from 'react-native'
import React, { useLayoutEffect } from 'react'

import { useNavigation } from '@react-navigation/native'

const HomeScreen = () => {

  const navigation = useNavigation();
  useLayoutEffect(() => {
    navigation.setOptions({
      headerShown: false,
    });
  }, []);
  const dataCate = [
    { id: '1', title: 'Item 1' },
    { id: '2', title: 'Item 2' },
    { id: '3', title: 'Item 3' },
    { id: '4', title: 'Item 4' },
    { id: '5', title: 'Item 5' },
  ];
  const renderItemCate = ({ item }) => (
    <View className="flex-row">
      <View className="flex-1 mt-2 w-24 h-28 items-center ">
        <Image source={{ uri:'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' }}
          className="w-20 h-20 rounded-full object-cover " 
          />
        <Text className="mt-2 font-semibold">{item.title}</Text>
      </View>


    </View>
  );

  return (
    <View className="bg-white flex-1 px-6 mt-8 ">
      <ScrollView  >
        <View>
        <View className="flex-row ">
          <View>
            <Text className="text-[#FF6813] text-[24px] font-semibold">FLPut App</Text>
            <Text className="text-[18px]">Hi Trung</Text>
          </View>
          <View className="right-0 absolute mt-2">
            <Image source={require('../assets/logoapp.png')} className="w-12 h-12 rounded-full" />
          </View>
        </View>
        <View className="mt-3">
          <Image source={require('../assets/banner.png')} className="max-w-full h-[180px] rounded-[40px]" />
        </View>
        <View className="mt-3">
          <TextInput
            className="border-2 border-[#ccc] h-[40px] rounded-full px-5 "
            placeholder="Tìm kiếm"
          />
        </View>
        <Text className="mt-3 text-[18px] font-semibold">Danh Mục</Text>
        <FlatList
          horizontal={true} // Thiết lập FlatList ngang
          data={dataCate}
          renderItem={renderItemCate}
          keyExtractor={(item) => item.id}
          contentContainerStyle={styles.container}
        />
        <Text className="mt-3 text-[18px] font-semibold">Top List</Text>
        </View>

      </ScrollView >
      <FlatList
          data={dataCate}
          renderItem={renderItemCate}
          keyExtractor={(item) => item.id}
          contentContainerStyle={styles.container}
        />
      
    </View>
    
  
  )
}

const styles = StyleSheet.create({
  container: {
    paddingVertical: 5,
  },

});
export default HomeScreen
