import { View, Text, Image, FlatList, TouchableOpacity } from 'react-native'
import React, { useLayoutEffect } from 'react'
import { useNavigation } from '@react-navigation/native'
import { Ionicons } from '@expo/vector-icons';

const DetailCategory = () => {
    const navigation = useNavigation();
    useLayoutEffect(() => {
        navigation.setOptions({
            headerShown: false,
        });
    }, []);
    const handleBackPress = () => {
        navigation.goBack();
    };

    const dataDetail = [
        { id: '1', title: 'Item 1', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
        { id: '2', title: 'Item 2', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
        { id: '3', title: 'Item 3', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
        { id: '4', title: 'Item 4', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
        { id: '5', title: 'Item 5', image: 'https://firebasestorage.googleapis.com/v0/b/appdoan-fd13e.appspot.com/o/7.jpg?alt=media&token=1cbf7d1b-0fb2-439c-be6a-86e02cb88809' },
    ];
    const renderItemCate = ({ item }) => (
        <TouchableOpacity onPress={() => navigation.navigate('DetailRes', { itemId: item.id })}>

            <View className="flex-row mt-2 mb-2">
                <Image source={{ uri: item.image }}
                    className="w-32 h-20  object-cover "
                />
                <View className="ml-2">
                    <Text className="font-semibold">{item.title}</Text>
                    <Text className="font-semibold">{item.title}</Text>
                    <Text className="font-semibold">{item.title}</Text>

                </View>

            </View>
        </TouchableOpacity>

    );
    return (
        <View className="flex-1 bg-white">
            <View className="flex-row bg-[#FF6813] h-12 mt-10">
                <View className="mt-2">
                    <TouchableOpacity onPress={handleBackPress}>
                        <Ionicons name="arrow-back" size={34} color="white" />
                    </TouchableOpacity>
                </View>
                <View className=" flex-1 items-center justify-center mr-6" >
                    <Text className="text-[20px] text-[#fff] font-semibold">CỬA HÀNG</Text>
                </View>
            </View>
            <View className="px-2">
                <FlatList
                    data={dataDetail}
                    renderItem={renderItemCate}
                    keyExtractor={(item) => item.id}

                />
            </View>
        </View>

    )
}

export default DetailCategory
