import React, { useState, useLayoutEffect } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet, SafeAreaView, TextBase } from 'react-native';
import { useNavigation } from '@react-navigation/native'


const RegisterScreen = () => {
  const [userName, setUserName] = useState('');
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [Repassword, setRePassword] = useState('');

  const navigation = useNavigation();
  useLayoutEffect(() => {
    navigation.setOptions({
      headerShown: false,
    });
  }, []);

  const handleLogin = () => {
  
  };
  const handleRegis = () => {
    navigation.navigate("Register")
  };
  return (
    <SafeAreaView className="flex-1">
      <View style={styles.container}>
        <Text style={styles.title}>FLPut App</Text>
        <Text style={styles.title1}>Chào bạn</Text>
        <TextInput
          style={styles.input}
          placeholder="Họ Tên"
          value={userName}
          onChangeText={setUserName}

        />
        <TextInput
          style={styles.input}
          placeholder="Số điện thoại"
          value={phone}
          onChangeText={setPhone}

          keyboardType='phone-pad'
          textContentType='telephoneNumber'
        />
        <TextInput
          style={styles.input}
          placeholder="Mật khẩu"
          value={password}
          onChangeText={setPassword}
          secureTextEntry
        />
        <TextInput
          style={styles.input}
          placeholder="Nhập lại mật khẩu"
          value={password}
          onChangeText={setPassword}
          secureTextEntry
        />
        <TouchableOpacity style={styles.button} onPress={handleLogin}>
          <View className="flex-1 items-center justify-center">
            <Text style={styles.buttonText}>Đăng Nhập</Text>
          </View>

        </TouchableOpacity>
        <View className="flex-row mt-2">
          <Text>Không có tài khoản? </Text>
          <Text onPress={handleRegis} className="text-[#FF6813] font-semibold">Đăng ký</Text>
        </View>
      </View>
      <View className="w-[70px] h-[70px] bg-[#ff9980] absolute rounded-full top-4 left-10"/>
      <View className="w-[40px] h-[40px] bg-[#FF6813] absolute rounded-full top-20 left-2"/>
      <View className="w-[70px] h-[70px] bg-[#ff9980] absolute rounded-full bottom-4 right-10"/>
      <View className="w-[40px] h-[40px] bg-[#FF6813] absolute rounded-full bottom-20 right-2"/>
    </SafeAreaView>
  )
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#FF6813',
  },
  title1: {
    fontSize: 20,
    marginBottom: 40,

  },
  input: {
    width: '80%',
    height: 50,
    borderWidth: 2,
    borderColor: '#ccc',
    borderRadius: 4,
    paddingHorizontal: 10,
    marginBottom: 20,
  },
  button: {
    backgroundColor: '#FF6813',
    width: '80%',
    height: 50,
    borderRadius: 4,
    paddingVertical: 10,
    paddingHorizontal: 20,

  },
  buttonText: {
    fontSize:16,
    alignItems: 'center',
    color: 'white',
    fontWeight: 'bold',
  },
});

export default RegisterScreen
